    public List buscarRangoRangoFechas(Connection conn, String fechaInicial, String fechaFinal) throws SQLException {

          List searchResults;

          boolean first = true;
          StringBuffer sql = new StringBuffer("SELECT * FROM ACCIDENTES FECHA_ACCIDENTE BETWEEN '"+ fechaInicial +"' AND '"+ fechaFinal+ "' ");
        
          sql.append(" ORDER BY ID_ACCIDENTES ASC ");

          // Prevent accidential full table results.
          // Use loadAll if all rows must be returned.
          if (first)
               searchResults = new ArrayList();
          else
               searchResults = listQuery(conn, conn.prepareStatement(sql.toString()));

          return searchResults;
    }


