    public void anular(Connection conn, RadicadoDeVehiculos valueObject) throws NotFoundException, SQLException {

        String sql =
            "UPDATE RADICADODEVEHICULOS SET ESTADO = ?, ID_USRANULA = ?, " + "FECHAANULA = ?, HORAANULA = ?, MOTIVOANULACION = ?" +
            "WHERE (ID_RADICADOVEH = ? ) ";
        PreparedStatement stmt = null;

        try {
            stmt = conn.prepareStatement(sql);
            if (Funciones.contarCadena(valueObject.getESTADO(), 3))
                stmt.setString(1, valueObject.getESTADO());
            else
                stmt.setNull(1, Types.VARCHAR);
            if (!Funciones.EnteroesNulo(valueObject.getID_USRANULA()))
                stmt.setInt(2, valueObject.getID_USRANULA());
            else
                stmt.setNull(2, Types.INTEGER);
            if (Funciones.esFecha(valueObject.getFECHAANULA()))
                stmt.setDate(3, Funciones.convFechaSSQL(valueObject.getFECHAANULA()));
            else
                stmt.setNull(3, Types.DATE);
            if (Funciones.esTime(valueObject.getHORAANULA()))
                stmt.setTime(4, Funciones.calendarToTime(Funciones.stringToCalendar(valueObject.getHORAANULA())));
            else
                stmt.setNull(4, Types.TIME);
            if (Funciones.contarCadena(valueObject.getMOTIVOANULACION(), 500))
                stmt.setString(5, valueObject.getMOTIVOANULACION());
            else
                stmt.setNull(5, Types.VARCHAR);
            stmt.setInt(6, valueObject.getID_RADICADOVEH());
            int rowcount = databaseUpdate(conn, stmt);
            if (rowcount == 0) {
                //System.out.println("Object could not be saved! (PrimaryKey not found)");
                throw new NotFoundException("Object could not be saved! (PrimaryKey not found)");
            }
            if (rowcount > 1) {
                //System.out.println("PrimaryKey Error when updating DB! (Many objects were affected!)");
                throw new SQLException("PrimaryKey Error when updating DB! (Many objects were affected!)");
            }
        } finally {
            if (stmt != null)
                stmt.close();
        }
    }

